(window.webpackJsonp=window.webpackJsonp||[]).push([[6],{304:function(n,t,l){"use strict";l.r(t);var e=l(35),component=Object(e.a)({},(function(){var n=this,t=n.$createElement;n._self._c;return n._m(0)}),[function(){var n=this,t=n.$createElement,l=n._self._c||t;return l("div",{staticClass:"container mx-auto p-20 "},[l("h1",{staticClass:"text-5xl text-center pb-8 text-yellow-700"},[n._v("अध्याय चार")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nतीसरे अध्याय में अर्जुन कर्मयोग संबंधी सभी प्रमुख बातें सुन चुका है। कर्म की गति बहुत\nगहन होती है। हमारे जन्म, मृत्यु, भोग आदि का निर्धारण हमारे कर्मों के अनुसार ही होता है।\nकर्म यज्ञमय होने से पूरा जीवन ही योगयुक्त हो जाता है।अत: योगेश्वर इस अध्याय में कर्मयोग\nके साथ यज्ञ का विस्तारपूर्वक वर्णन करेंगे। योगेश्वर पहले तीन श्लोकों में अर्जुन को कहते हैं कि\nकर्मयोग कोई नई चीज नहीं है। यह सृष्टि में अनादि काल से चलता आ रहा है।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\n‌कर्मयोग मैंने सूर्य को सुनाया,"),l("br"),n._v("\nअनादि काल से चलता आया। - 1\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nभगवान् श्रीकृष्ण कहते हैं कि मैं कोई नवीन ज्ञान या सिद्धांत का प्रतिपादन नहीं कर रहा हूँ।\nयह अविनाशी योग सदा से चलता आया है। राजा-महाराजाओं ने परंपरा से इसे एक-दूसरे से\nपाया था। मैंने यह योग सबसे पहले सूर्य को सुनाया था। सूर्य ने अपने पुत्र मनु को और मनु ने\nराजन् इक्ष्वाकु को यह योग दिया था। श्लोक के भाव के अनुसार सूर्य का अर्थ प्रतापी राजाओं से\nहै। जगद्गुरु शंकराचार्य ने भी गीता भाष्य में इस श्लोक के सन्दर्भ में सूर्य का अर्थ जगत्\nप्रतिपालक क्षत्रिय ही कहा है। इक्ष्वाकु सूर्य वंश के प्रथम राजा माने जाते हैं। इसी वंश में\nप्रतापी राजा रघु, अज, दशरथ और राम का जन्म हुआ था। उन दिनों राजतंत्र पर धर्मतंत्र का\nनियंत्रण था। भगवद्गीता प्रशासन की पथ-प्रदर्शिका अर्थात् संविधान पुस्तिका के रूप में मान्य\nथी।आज भारत एक धर्मनिरपेक्ष राज्य है। अतः भगवद्गीता को प्राचीन संवैधानिक मान्यता\nदेना संभव नहीं है। \n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nमाघ मास की एकादशी को प्रतिवर्ष गीता जयंती धूमधाम से मनाई जाती है। 25 नवंबर,\n2017 को भारत के माननीय राष्ट्रपति श्री रामनाथ कोविंद जी ने अंतर्राष्ट्रीय गीता महोत्सव\nके अवसर पर कहा-\"गीता व्यक्ति के द्वंद्व का समाधान करती है कि क्या सही है और क्या गलत\nहै। मेरा मानना है कि जो भी गीता के संदेश का पालन करेगा, वह 'द्वंद्व' से मुक्त रहेगा, शांत\nरहेगा तथा जीवन में सफल होगा। भगवद्गीता असुरक्षा, तनाव और भ्रम के भँवर में फँसे\nयुवाओं को उससे बाहर लाने के लिए एक आध्यात्मिक रास्ता उपलब्ध कराती है। सभी लोगों\nको अपने घरों के साथ-साथ अपने मन में भी हर दिन गीता महोत्सव मनाना चाहिए।\" इसी\nक्रम को जारी रखते हुए योगेश्वर कहते हैं–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nराजाओं का जीवन था योगयुक्त,"),l("br"),n._v("\nकाल प्रभाव से हुआ योग लुप्त। - 2"),l("br"),n._v("\nआज सुनाया ज्ञानयोग पुरातन,"),l("br"),n._v("\nतू है मेरा शिष्य प्रियतम। - 3\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nहे अर्जुन! सभी राजा-महाराजाओं का जीवन आदर्श और योगमय होता था। धीरे-धीरे समय के\nप्रभाव से यह योग धूमिल पड़ गया। रघुकुल के सभी राजा प्रतापी और धर्माचारी थे।\nभरतवंशी राजाओं का जीवन भी नीतियुक्त था। राजा प्रसेनजित, बिम्बिसार और अजातशत्रु\nभी भगवान् बुद्ध के अनुयायी थे। धीरे-धीरे यह परंपरा लुप्त हो गई। गीता का थोड़ा अंश भी\nयदि भारत की जनशक्ति ने फिर से अपना लिया, तो भारत उठकर खड़ा हो जाएगा और\nसंसार में सूर्य की तरह चमकता नजर आएगा।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nअर्जुन! तू मेरा प्रिय शिष्य है। आज तुझे वही प्राचीन योग सुना रहा हूँ। ज्ञान सदा पुराना ही\nहोता है। बदलते समय के अनुसार उसे अभिव्यक्त करने के तरीके, भाषा-शैली आदि बदल\nजाते हैं। मानस के उत्तरकांड में काकभुशुण्डि जी कहते हैं-"कहेऊँ परम पुनीत इतिहासा।" (मैंने\nयह परम पुनीत इतिहास कहा।) सत्य हर देश, काल में एक जैसा ही रहता है, उसमें परिवर्तन\nके लिए कोई स्थान नहीं होता है। असत्य आधारहीन होता है, इसलिए वह आता-जाता रहता\nहै। अर्जुन भगवान् का प्रिय शिष्य है। शिष्यत्व की कसौटी समर्पण है। बुद्ध-आनंद, चाणक्य-\nचंद्रगुप्त, श्री रामकृष्ण परमहंस-विवेकानंद, महर्षि विरजानंद और स्वामी दयानंद की जोड़ी\nसमर्पण का परिणाम थी। भगवान् द्वारा पूर्व जन्मों में दिए गए उपदेश की बातें सुनकर अर्जुन\nके मन में जिज्ञासा उठी और वह पूछने लगा–\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nसूर्य भगवन् आपसे ज्येष्ठ,"),l("br"),n._v("\nकैसे संभव यह उपदेश? - 4"),l("br"),n._v("\nअर्जुन मेरे तेरे जन्म अनेक,"),l("br"),n._v("\nमुझे स्मरण है जन्म प्रत्येक। - 5\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nअर्जुन का योगेश्वर से प्रश्न है कि आपका जन्म तो अभी का है, सूर्यवंशी राजाओं की तो अनेक\nपीढ़ियाँ बीत चुकी हैं। अर्जुन कृष्ण का संवाद संपूर्ण मानव-जाति के कल्याण के लिए है। अर्जुन\nभगवान् श्रीकृष्ण के योग-बल और शक्तियों से भली-भाँति परिचित थे। उन्होंने जानबूझकर\nऐसे प्रश्न पूछे हैं, जिनसे युगों-युगों तक जनसामान्य की योग संबंधी समस्याओं का समाधान\nहोता रहे। भगवान् अर्जुन के प्रश्न का उत्तर देते हुए कहते हैं कि मेरे और तेरे अनेक जन्म हो\nचुके हैं। मैं उन सभी को जानता हूँ, परंतु योग में पूरी तरह प्रवृत्त न होने के कारण तुझे वे याद\nनहीं हैं। भगवान् बुद्ध को अपनी हजारों जन्मों की बातें याद थीं। महावीर जैन, आदि गुरु\nशंकराचार्य, गुरु नानक देव, श्री रामकृष्ण परमहंस जैसे महात्माओं ने पुनर्जन्म की मान्यता को\nस्वीकार किया है। महर्षि पतंजलि ने भी योग-दर्शन (3.18) में पुनर्जन्म की पुष्टि करते हुए\nकहा है–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nसंस्कारसाक्षात्करणात् पूर्वजातिज्ञानम्।।\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\n“संस्कार का साक्षात्कार कर लेने से पूर्वजन्म का ज्ञान हो जाता है।” पुनर्जन्म का सिद्धांत\nवैज्ञानिकों के लिए भी चुनौती बना हुआ है। हमारे प्राचीन ऋषियों ने शरीर को प्रयोगशाला\nऔर मन को यंत्र बनाकर जीवन-मृत्यु के रहस्यों को पूर्ण रूप से जान लिया था। यहाँ प्रश्न पैदा\nहोता है कि क्या हम भी अपने पूर्वजन्मों को जान सकते हैं? योग का राजमार्ग बिना किसी\nभेदभाव के सबके लिए खुला हुआ है। संसार की भागदौड़ में हम जितना समय लगाते हैं, यदि उससे आधा समय भी योगाभ्यास में लगा दें, तो कुछ ही वर्षों में योग के आशातीत परिणाम\nअनुभव कर सकते हैं। आगे के चार श्लोकों में योगेश्वर अपने पूर्व जन्मों की विशेषता बताते हैं।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nमैं हूँ जन्म रहित अविनाशी,"),l("br"),n._v("\nसबका नियामक घट-घट वासी।"),l("br"),n._v("\nअधीन कर लेता अपनी प्रकृति,"),l("br"),n._v("\nयोगमाया से प्रकट करता शक्ति। - 6\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयोगेश्वर ने इस श्लोक में अपने को जन्मरहित, अविनाशी, घट-घट वासी और अपनी योगमाया\nसे स्वतंत्र रूप से प्रकट होने वाला कहा है। भारतीय योग मनुष्य को नर से नारायण बनने की\nकला सिखाता है। भगवान् श्रीकृष्ण योगियों के राजा हैं। कोटि-कोटि ब्रह्माण्ड पर उनका\nअधिकार है। उनके ज्ञान से परे कुछ भी नहीं है। चित्त के स्थिर होने पर साधक ब्रह्माण्ड के\nरहस्यों का ज्ञाता हो जाता है। महर्षि पतंजलि योग-दर्शन (1.40) में कहते हैं–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nपरमाणुपरममहत्त्वान्तोऽस्य वशीकार:।।\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\n“अभ्यास करते-करते साधक अपने चित्त को सूक्ष्म परमाणुओं से लेकर बड़े-से-बड़े पदार्थ तक\nजहाँ चाहे, वहाँ तत्काल स्थिर कर सकता है। उसका अपने चित्त पर पूरा अधिकार हो जाता\nहै।” मानव देह सीमित है, परंतु उसमें निहित परम तत्त्व असीमित और अनंत शक्तियों से युक्त\nहै।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nमनुष्य का जन्म उसके मन का परिणाम है। हमारे मन की इच्छाओं, कामनाओं और वृत्तियों के\nअनुसार प्रकृति के शाश्वत नियम हमें जन्म लेने के लिए बाध्य करते हैं। भगवान् श्रीकृष्ण शुद्ध\nस्वरूप हैं, वे प्रकृति की शक्ति का अपनी इच्छानुसार प्रयोग करके संसार के कल्याण के लिए\nप्रकट होते हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nगीता में भगवान् श्रीकृष्ण ने जहाँ भी 'मैं' शब्द का प्रयोग किया है, उसका अर्थ अत्यंत व्यापक\nहै। अपने व्यक्तिगत ‘मैं’ को समष्टि के साथ समन्वित करना ही योग है। इसी विराट् ‘मैं’ की\nओर उन्होंने बार-बार संकेत किया है। प्रकृति के नियमों को धारण करने से ही यह संभव हो\nसकता है। अनुशासन, निष्पक्षता, सत्य, नित्य-निरंतर यज्ञ, अपरिग्रह, संतोष, तप, सरलता,\nशांति आदि नियम प्रकृति में निहित हैं। इसी क्रम को जारी रखते हुए योगेश्वर अपने अवतार\nरूप में प्रकट होने का कारण बताते हैं।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nजब-जब होती धर्म की हानि,"),l("br"),n._v("\nबढ़ जाते असुर अभिमानी।"),l("br"),n._v("\nरक्षित करता मैं साधुजन,"),l("br"),n._v("\nनष्ट हो जाते सब दुर्जन। - 7"),l("br"),l("br"),n._v("\nयुग–युग प्रकट होता रहता,"),l("br"),n._v("\nधर्म स्थापना मैं हूँ करता। - 8"),l("br")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयोगेश्वर कहते हैं कि अधर्म, अनाचार, दुष्टता, भय, आतंक चिरस्थाई नहीं हो सकते। जब\nस्थिति मनुष्य के हाथ से बाहर निकल जाती है,तो वे स्वयं देह धारण करके मनुष्यों के बीच में\nप्रकट होकर संतुलन स्थापित करते हैं। धरती पर अनेक बार महासंकट पैदा हुआ, परंतु शक्ति\nने अवतरित होकर उसे निरस्त कर दिया। आज फिर से हमने भौतिक प्रगति के नाम पर\nविनाश के साधन चारों तरफ जमा कर लिए हैं। दार्शनिक, विद्वान्, मनीषी, योगी, राजर्षि,\nवैज्ञानिक इस समस्या का समाधान करने में स्वयं को असमर्थ महसूस कर रहे हैं। लगता है कि\nयदि हम सब इसी प्रकार दौड़ते रहे, तो मनुष्य जल्दी ही अपना अस्तित्व गवाँ बैठेगा।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nअब प्रश्न पैदा होता है कि क्या भगवान् अपनी इस प्रतिज्ञा को आज के युग में पूरा करेंगे?\nप्रकृति अपने आप में पूर्ण है। असंतुलन को संतुलित करने के लिए उसके कोष में पर्याप्त शक्ति\nमौजूद है। भगवान् राम, कृष्ण, बुद्ध जैसी महान् आत्माएँ देह धारण किए बिना ही संकल्प\nबल से हमारे मन को नियमित कर धर्म की स्थापना कर सकती हैं। परंतु स्थूल देहधारियों के\nशिक्षण के लिए दृष्टांत और नमूनों की आवश्यकता अनिवार्य है। किसी घटना या पदार्थ को\nप्रत्यक्ष देखकर ही हमारी जिज्ञासा का पूर्ण समाधान होता है। भगवान् स्वयं अवतरित होकर\nअपने चरित्र और कर्मों के द्वारा हमारे मन में धर्म के प्रति दृढ़ विश्वास पैदा करते हैं। भगवान्\nका असली अवतरण मनुष्यों की चेतना में अवतरित होकर युग के प्रवाह को बदलना है। यह\nकार्य योगशक्ति के सूक्ष्म महाप्रयोगों के द्वारा होता है। भगवान् बुद्ध ने चेतना के इसी वैज्ञानिक\nप्रयोग के द्वारा मनुष्यों के मन को नई दिशा देकर एक नये युग का सूत्रपात किया था।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nआज का युग विज्ञान का युग है। बुद्धि के खेल ने प्रकृति के साथ खिलवाड़ किया है। अतः\nभगवान् का अवतरण भी इसी के अनुरूप होना चाहिए। गायत्री के महान् साधक और गायत्री\nपरिवार के संस्थापक पंडित श्री राम शर्मा आचार्य जी ने अगस्त 1979 की 'अखण्ड ज्योति\nपत्रिका' में लिखा है-\"अपने युग में भगवान् की सत्ता प्रज्ञावतार के रूप में प्रकट हो रही है। वह\nव्यक्ति के रूप में नहीं, शक्ति के रूप में प्रकट होगी। अपने युग में जो महाभारत लड़ा जाएगा\nवह विशुद्ध चेतना क्षेत्र का होगा। उसमें विचार, मान्यताएँ और आकांक्षाएँ ही उखाड़ी और\nजमाई जाएँगी। गायत्री महामंत्र में उन सभी तत्त्वों का समावेश है, जो सद्भाव संपन्न\nआस्थाओं के निर्माण एवं अभिवर्धन का प्रयोजन पूरा कर सकें।\"\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nमहर्षि अरविंद घोष ने भी \'योग-समन्वय’ में इसी प्रकार का संदेश दिया है-"हम एक ऐसे युग\nमें निवास कर रहे हैं, जो भावी सृष्टि की प्रसव-वेदना से व्याकुल है।" इस समय धरती पर जो\nउथल-पुथल मची हुई है, वह अवतार के द्वारा मनुष्यों को नियमित करने की प्रक्रिया का ही\nहिस्सा है। इस वेदना के बाद सुख का साम्राज्य आना सुनिश्चित है। कलियुग के इस महावतार\nको जानने-पहचानने में मनुष्य-जाति को बहुत समय लग सकता है। इसके पश्चात् योगेश्वर\nअपने दिव्य जन्म, कर्म का वर्णन करते हैं।\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nअर्जुन दिव्य मेरे जन्म, कर्म,"),l("br"),n._v("\nऋषि–महर्षि जानते यह मर्म।"),l("br"),n._v("\nपुनर्जन्म नहीं लेता श्रद्धावान्,"),l("br"),n._v("\nउसे प्राप्त हो जाते भगवान्। - 9 \n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 9 का अर्थ गहन है। योगेश्वर कहते हैं कि मेरे सभी जन्म और कर्म दिव्य हैं, जो इस रहस्य\nको जान लेता है उसका पुनर्जन्म नहीं होता है। यहाँ हमारे लिए दिव्य का अर्थ जान लेना\nआवश्यक है। दिव्यता का अर्थ अक्सर चमत्कार समझ लिया जाता है। ध्यान, साधना के द्वारा\nजब हमारा चित्त (प्राण) पूर्ण शुद्ध हो जाता है, तो हमें अनुभव होगा कि यह शरीर ब्रह्मस्वरूप\nहै। योग हमें ब्रह्माण्ड से जोड़ता है। ब्रह्म में जो शक्तियाँ हैं, वे मनुष्य में भी हैं। इस एकरूपता\nको अनुभव करने से ही हमें समझ आता है कि भगवान् कृष्ण के जन्म और कर्म दिव्य हैं। वे\nकिसी भोगवश नहीं, एक महान् उद्देश्य की पूर्ति के लिए धरती पर अवतरित होते हैं। वे कंस,\nशिशुपाल, जरासंध का वध करें या गोपियों के साथ रास रचाएँ या महाभारत के युद्ध में अर्जुन\nका रथ चलाएँ, इन सबके पीछे उनकी दिव्यता झलकती है। संसार के कल्याण के लिए वे\nयदुवंशियों का भी संहार कर देते हैं। दिव्यता धारण करके ही दिव्य को समझा जा सकता है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nमहर्षि अरविंद घोष ने ‘दिव्य जीवन’ में इसके विषय में लिखा है-"दिव्य आत्मा का जन्म\nसच्चिदानंद की सचेतन लीला में हमेशा स्वतः पूर्ण होगा। वह अपनी सत्ता में शुद्ध, अनंत,\nस्वयंभू और अपनी संभूति में अमर जीवन की मुक्त लीला होगी। जिस पर जन्म-मरण या\nशरीर परिवर्तन का आक्रमण न होगा क्योंकि वह अज्ञान के बादलों में न होगा और न ही\nहमारी भौतिक सत्ता के अंधकार में फँसा होगा।" कर्मों का जीवन पर प्रभाव बताते हुए\nयोगेश्वर अगले श्लोक में कहते हैं–\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nआसक्ति जब जाती छूट,"),l("br"),n._v("\nज्ञानी पाता मेरा स्वरूप। - 10"),l("br"),n._v("\nसब करते मेरा अनुसरण,"),l("br"),n._v("\nभक्तों को देता मैं शरण। - 11\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 10 में योगेश्वर कहते हैं कि पहले भी अनेक लोग ज्ञान और तप के द्वारा मेरे स्वरूप को\nप्राप्त कर चुके हैं। मेरी शरण में आकर मेरे अभिन्न अंग बन गए हैं। वे आसक्ति, भय और क्रोध\nसे पूर्णतः मुक्त हो चुके हैं। अब उनके गिर जाने का भय पूर्णतः समाप्त हो गया है। पूर्णता को\nप्राप्त किए बिना आसक्ति, क्रोध, भय आदि के हमले का डर बना रहता है। भगवान् एक कुशल\nमनोवैज्ञानिक की भाँति क्रमशः स्थूल से सूक्ष्म की ओर बढ़ रहे हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nधरती के अंदर जल और बहुमूल्य संपदा भरी पड़ी है, लेकिन उसे पाने के लिए प्रयत्न तो करना\nही पड़ता है। इसी प्रकार भगवान् हमारे अंदर हैं, हम सारी शक्तियों के स्वामी हैं, परंतु उसे\nपाने के लिए प्रकृति की तरह अनुशासन में रहना और उसके नियमों का पालन करना\nअत्यावश्यक है। स्वामी विवेकानंद ने राजयोग में लिखा है-"साधना के द्वारा ही मेरी बात की\nसत्यता का प्रमाण मिलेगा। मैं इस विषय में कितनी भी युक्तियों का प्रयोग क्यों न करूँ पर वे\nतुम्हारे लिए तब तक प्रमाण नहीं होंगी, जब तक तुम स्वयं प्रत्यक्ष न कर लोगे। परंतु इसके\nअनुभव के लिए कठोर अभ्यास आवश्यक है।” योगेश्वर कहते हैं कि जैसा करोगे, वैसा ही फल\nपाओगे। पाँच-दस मिनट की पूजा से जरा-सा ही लाभ मिलेगा। कीमत तो हर चीज की\nचुकानी ही पड़ती है। मीराबाई डंके की चोट पर कहती हैं–\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v('\n"माई री मैं तो लीनो गोविंद मोल।'),l("br"),n._v("\n\nकोई कहै महँगो, कोई कहै सोंधो,"),l("br"),n._v("\nलियो री तराजू तोल।। - (मीरा प्रेम दीवानी)\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nमीराबाई ने कृष्ण को पाने के लिए सर्वस्व न्योछावर कर दिया था। परमात्मा सब कुछ हैं,\nअतः सब कुछ समर्पण करके ही उन्हें पाया जा सकता है। परमात्मा शक्ति स्वरूप हैं और हम\nसब भिन्न-भिन्न तरीके से शक्ति की ही उपासना, साधना और आराधना करते हैं। जितना\nहमारा प्रेम व्यापक होता जाएगा, हम उतना ही भगवान् कृष्ण के नजदीक पहुँचते चले\nजाएँगे। कर्मयोग का महत्त्व जानते हुए भी हम कर्मफल की इच्छा क्यों रखते हैं? इसके विषय\nमें योगेश्वर कहते हैं–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nफल के इच्छुक करते देव आराधना,"),l("br"),n._v("\nशीघ्र ही फलती उनकी यह साधना। - 12\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nकर्मफल चाहने वाले अपनी पूजा, जप-तप के बदले में सांसारिक वस्तुएँ, धन-धान्य, पुत्र आदि\nकी कामना करते हैं। देवी-देवता उनकी कामनाओं की पूर्ति शीघ्रता से कर भी देते हैं। संसार\nकी वस्तुएँ हमें परिश्रम से मिल जाती हैं। यदि जीवन में कुछ अभाव रह भी जाता है, तो हमें\nसंतोष कर लेना चाहिए। प्रकृति नियमों से चल रही है। हमारी पूजा-पत्री के बदले में यदि कुछ\nमिल भी जाता है, तो देर-सबेर हमें उसकी कीमत चुकानी पड़ती है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nराजा के पास जाकर यदि माँगना ही है, तो संसार की सबसे कीमती चीज माँगनी चाहिए।\nइसी प्रकार भगवान् से भी ब्रह्माण्ड की सबसे बहुमूल्य चीज भगवान् को ही माँग लेना\nचाहिए। अन्य सभी वस्तुएँ तो हमारे जीवन के साथ मिट जाएँगी, भगवान् के साथ तो जन्म-\nजन्मांतर का संबंध रहेगा।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nसाधना की प्रारंभिक अवस्था में बच्चों की तरह थोड़ा-बहुत पाने का लालच मन में आ सकता\nहै। परंतु जब हमारा ध्यान प्रगाढ़ हो जाएगा, तो हमें ईश्वरीय नियम समझ में आने लग जाएँगे\nऔर सांसारिक लाभ की वस्तुएँ माँगने की लालसा भी स्वतः छूट जाएगी।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nकबीरा मन निर्मल भया, जैसे गंगा नीर।"),l("br"),n._v("\nपाछे लागा हरि फिरे, कहत कबीर, कबीर।।\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nसाधना में यदि समर्पण हो, तो कबीर की तरह हमारे सामने भी भगवान् अपना खजाना\nखोलकर रख देंगे। परिवार के अभाव दूर करने के लिए स्वामी विवेकानंद अपने गुरु श्री\nरामकृष्ण परमहंस के कहने पर रात के अँधेरे में माँ काली के मंदिर में प्रार्थना के लिए गए।\nजगदंबा को देखते ही वे संसार को भूल गए और कह उठे-"माँ! मुझे विवेक दो, ज्ञान दो, भक्ति\nदो।" यदि वे हम लोगों की तरह सांसारिक वस्तुएँ माँग बैठते, तो महर्षि के पद तक नहीं पहुँच\nपाते। अगले श्लोकों में कर्मयोग का ही वर्णन है।\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nगुण कर्म विधान बनाया,"),l("br"),n._v("\nचार वर्ण संसार रचाया।"),l("br"),n._v("\nसृष्टि रचना मैं ही करता,"),l("br"),n._v("\nफिर भी अर्जुन मैं हूँ अकर्ता। - 13\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयोगेश्वर का कहना है कि मैंने गुण-कर्म के आधार पर ब्राह्मण, क्षत्रिय, वैश्य और शूद्र आदि\nचार वर्णों की रचना की है। सृष्टि का रचयिता होने पर भी मैं उससे अप्रभावित रहता हूँ।\nसमाज में असभ्य, हिंसक वृत्ति के लोग सदा से रहे हैं। भगवान् ने सबके विकास के लिए\nसमाज को चार वर्गों में बाँट दिया, ताकि कोई भी पिछड़ा या असभ्य न रहे। भगवान् की इस\nसुंदर व्यवस्था को अपने-अपने स्वार्थ में हमने तोड़-मरोड़कर घृणित और त्याज्य बना दिया है।\nसंविधान की दृष्टि में हम सब बराबर हैं, परंतु जाति-प्रथा का विषकारक प्रभाव किसी से\nछिपा नहीं है। जन्म लेते ही किसी को जाति के आधार पर अछूत या नीच समझ लेना\nअसभ्यता और अमानवीयता की पराकाष्ठा है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nवेद, उपनिषदों और पुराणों में अनेक प्रमाण हैं, जिनके अनुसार जन्म के आधार पर नहीं, गुण\nऔर कर्म के अनुसार समाज का वर्गीकरण किया गया था। यजुर्वेद (26.2) में सभी वर्णों को\nवेद पढ़ने का अधिकार है। बृहदारण्यकोपनिषद् (1.4) से भी इस बात की पुष्टि होती है कि\nपहले केवल एक वर्ण था, बाद में सुविधानुसार चार वर्ण बने। मनुस्मृति (10.65) के अनुसार\nगुण और कर्मों के आधार पर शूद्र ब्राह्मण और ब्राह्मण शूद्र बन सकता है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nमहाभारत (शांतिपर्व-188.10) में भी वर्णन है कि पहले वर्णों में कोई अंतर नहीं था। ब्रह्मा से\nउत्पन्न होने के कारण सभी ब्राह्मण थे। भगवान् बुद्ध भी धम्मपद में गुणों के आधार पर वर्ण-\nव्यवस्था का समर्थन करते हुए कहते हैं-"जिसके अंदर ‘मैं’ और ‘मेरे’ का भाव नहीं है, जो\nनिर्भय और अनासक्त है, उसे मैं ब्राह्मण कहता हूँ।" ऋग्वेदादिभाष्यभूमिका में ऋषि दयानंद\nका कथन है-"मूर्ख का नाम शूद्र और अतिमूर्ख का नाम अतिशूद्र है। मनुष्य-जाति सबकी एक\nही है।”\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nसभी शास्त्रीय प्रमाणों और अनेक महान् आत्माओं के अथक प्रयासों के बावजूद जाति-प्रथा\nहमारे मन में रची-बसी हुई है। डॉ. सर्वपल्ली राधाकृष्णन् ने भगवद्गीता में इस श्लोक का भाष्य\nकरते हुए लिखा है-"चातुर्वर्ण्य व्यवस्था मानवीय विकास के लिए बनाई गई है। जाति व्यवस्था\nकोई परम वस्तु नहीं है। इतिहास की प्रक्रिया में इसका स्वरूप बदलता रहा है।" सूचना-क्रांति\nके कारण हम सब इतने नजदीक आ गए हैं, मानो एक छतरी के नीचे खड़े हैं। अतः इस\nव्यवस्था में आमूल-चूल परिवर्तन की आवश्यकता है। इसके लिए किसी एक वर्ग को नहीं, हम\nसभी को आगे आना होगा। अपने-अपने स्वार्थ, हित और लाभ को त्यागकर राष्ट्र-यज्ञ में आहुति\nदेनी होगी। इसी क्रम को जारी रखते हुए योगेश्वर कहते हैं–\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nकर्म नहीं करते मुझे लिप्त,"),l("br"),n._v("\nकर्मफल से मैं रहता मुक्त।"),l("br"),n._v("\nजो जान लेता तत्त्व ज्ञान,"),l("br"),n._v("\nमिट जाता उसका अज्ञान। - 14\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 14 में भगवान् कृष्ण कहते हैं कि मैं कर्म और कर्मफल से मुक्त रहता हूँ। जो इस तत्त्व को\nजान लेगा, मेरी तरह वह भी कर्मफल के बंधन से मुक्त रहेगा। कर्मफल प्रकृति का अटल नियम\nहै। कर्म में आसक्ति के कारण हमें कर्मफल भोगना पड़ता है। मन हमारे विचारों को जबरदस्त\nतरीके से संजोकर रखता है और विशेष अवधि के बाद कर्मफल के रूप में हमें वापस दे देता है।\nआत्मा हमारे शरीर में शाश्वत रोशनी है। यह पूरे शरीर का केंद्र है। यदि ध्यान के द्वारा मन इस केंद्र तक पहुँच जाए, तो वह भी आत्मा की तरह कर्मों के बंधन से मुक्त हो जाएगा। अतः\nकर्मबंधन हमारी अज्ञानता का परिणाम है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nमहर्षि रमण का कथन है-"कर्मनियम तब तक अमल में रहता है, जब तक मनुष्य स्वयं को\nआत्मा से, ईश्वर से भिन्न मानता है। वे कहते थे कि अज्ञान दशा में सभी जीवों को पूर्व\nनिर्धारित क्रियाओं एवं अनुभवों की श्रेणी से गुजरना पड़ता है। एक बार मनुष्य आत्मा का\nअनुभव कर ले, तो कर्मफल भोगने वाला कोई नहीं रहेगा और इस कारण कर्म नियम का पूरा\nसिद्धांत व्यर्थ बन जाएगा।" इसके पश्चात् योगेश्वर अर्जुन को कर्मयोग के विषय में बताते हैं।\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nतत्त्व - ज्ञान का महत्त्व सनातन,"),l("br"),n._v("\nइसी अनुसार करो कर्म संपादन। - 15\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयोगेश्वर कहते हैं कि कर्म के संबंध में जो तत्त्वज्ञान मैं बता रहा हूँ, वह सनातनी है। हमारे\nपूर्वजों ने भी इसी का अनुसरण किया था। यदि हम संसार के इतिहास पर दृष्टिपात करें, तो\nईसा, राम, कृष्ण, बुद्ध, ऋषि, मुनियों, महान् संतों और समाज सुधारकों का एकमात्र हथियार\nभगवान् कृष्ण द्वारा अन्वेषित कर्मयोग ही रहा है। अतः भगवान् कृष्ण अर्जुन के माध्यम से हम\nसभी को इसी मार्ग पर चलने के लिए कह रहे हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nमहर्षि अरविंद घोष ‘योग समन्वय’ में भगवान् कृष्ण के इस कर्मयोग विषयक ज्ञान के महत्त्व\nको स्वीकार करते हुए लिखते हैं-"आध्यात्मिक कर्मों का सबसे महान् दिव्य सत्य जो आज तक\nमानव-जाति के लिए प्रकट किया गया है, अथवा कर्मयोग की पूर्णतम पद्धति जो अतीत में\nमनुष्य को विदित थी, भगवद्गीता में पाई जाती है।" भगवान् कृष्ण द्वारा प्रदत्त यह कर्मयोग\nका ज्ञान आज भी उतना ही तर्कसंगत है, जितना महाभारत काल में था। भारत का स्वतंत्रता\nआंदोलन भी नि:स्वार्थी बलिदानियों के नेतृत्व में लड़ा गया था। आज जो अनगिनत समस्याएँ\nउठ खड़ी हुई हैं, उनका निदान भी तप से उपजे कर्मयोगियों द्वारा ही होगा। आगे के श्लोकों में\nकर्म का सूक्ष्म रूप वर्णित है।\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nकर्म,अकर्म का भेद कठिन,"),l("br"),n._v("\nज्ञानी को भी लगता जटिल।"),l("br"),n._v("\nअब मैं कर्म तत्त्व कहूँगा,"),l("br"),n._v("\nबंधन मुक्त तुझे करूँगा। - 16"),l("br"),l("br"),n._v("\nकर्म,अकर्म जानना चाहिए,"),l("br"),n._v("\nविकर्म तत्त्व भी पहचानिए।"),l("br"),n._v("\nबड़ी गहन है कर्म गति,"),l("br"),n._v("\nसमझ न पाए इसे मति। - 17"),l("br"),l("br"),n._v("\nकर्म में अकर्म योगी जानते,"),l("br"),n._v("\nअकर्म में कर्म वे पहचानते। - 18\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nये तीनों श्लोक कर्म तत्त्व के संबंध में अति महत्त्वपूर्ण हैं। कर्म क्या है? अकर्म क्या है? विकर्म\nक्या है? इन तीनों का भेद जानकर इन श्लोकों का भावार्थ समझा जा सकता है। सबसे पहले कर्म को समझ लेते हैं, क्योंकि बंधन, मुक्ति, सुख, दुःख की चाबी इसी में निहित है। कर्म से ही\nसंसार और जीवन चल रहा है। हमारे शरीर में खाना, पीना, सोना, चलना, उठना, बैठना\nआदि क्रियाएँ स्वाभाविक रूप से चलती रहती हैं। इन्हें करने के लिए मन को कोई विशेष\nप्रयत्न नहीं करना पड़ता है। ये बिना संकल्प के होती रहती हैं। अतः इन्हें कर्म नहीं क्रियाएँ\nकहा जाता है। ये फलदायी नहीं होती हैं। जब हम किसी कार्य को मनोयोगपूर्वक करते हैं, तो\nवह कर्म कहा जाता है। जीवन, मृत्यु और समस्त दुःखों का कारण संकल्पपूर्वक किए गए कर्म\nहैं। कर्म हमारे अहंकार और कामनाओं से लिप्त होता है। अतः इसे ही ऋषियों ने बंधन की जड़\nमाना है। महर्षि अरविंद रचित ‘योग समन्वय’ में वर्णित है-"कर्मयोग के पथ में कर्म ही सबसे\nपहले खोलने योग्य ग्रंथि है, हमें इसे वहीं से खोलने का प्रयत्न करना होगा जहाँ कामना और\nअहम् भाव में यह मुख्य रूप से बंधी हुई है।"\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\n‘गहना कर्मणो गतिः।’—भगवान् कृष्ण कहते हैं कि कर्म की गति बड़ी गहन है। अवचेतन मन\nमें सभी कर्म बीज रूप में जमा होते जाते हैं और जन्म-जन्मांतर तक सुख-दुःख के रूप में\nफलित होते रहते हैं। कर्म के इस सूक्ष्म खेल को ध्यान की गहराई में जाकर ही समझा जा\nसकता है। महर्षि पतंजलि साधनपाद के सूत्र 12 में इसकी पुष्टि करते हुए कहते हैं–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nक्लेशमूलः कर्माशयो दृष्टादृष्टजन्मवेदनीयः।।\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\n“क्लेश मूलक कर्म-संस्कार का समुदाय वर्तमान और भविष्य दोनों प्रकार के जन्मों में भोगा\nजानेवाला है।”अकर्म वे कर्म होते हैं, जो ब्रह्म द्वारा ब्रह्म के लिए होते हैं। सरल भाषा में इसे\nइस प्रकार कहा जा सकता है कि कर्म को भगवान् का मानकर करने से उसका फल सुख-दुःख\nन होकर ब्रह्म प्राप्ति ही होता है। अकर्म बुद्ध मन, कृष्ण मन में घटित होता है। अकर्म में कर्म\nक्या होता है? हम सोचते हैं कि भगवान् सो रहे हैं, आराम कर रहे हैं, इस अकर्म-सी लगने\nवाली अवस्था में भी उनकी चेतना विश्व-स्तर पर अनेक कार्य करती रहती है। महर्षि रमण के\nमौन को पशु भी समझ लेते थे।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nअकर्म की स्थिति तक पहुँचने के लिए अहंकार को जड़ से काटना होगा। अहंकार ही हमारे और\nभगवान् के बीच में विशालकाय पर्वत है। अहंकार, तृष्णा, कामना की जड़ें पाताल से भी\nगहरी हैं। हम सोचते हैं कि बहुत तप कर लिया और अहंकार को मिटा दिया, परंतु यह अवसर\nपाते ही सूखी घास के समान पुनः जिंदा हो उठता है। भगवान् बुद्ध का कथन है-"जैसे जड़ के\nबिल्कुल नष्ट न होने पर कटा हुआ वृक्ष फिर जग जाता है, वैसे ही तृष्णा के जड़ से खत्म न होने\nपर दुःख बार-बार उत्पन्न होता रहता है।”\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nकर्मयोगी होने के लिए हमें कर्तापन के अभिमान का त्याग करना होगा। फिर हम कर्म में अकर्म\nऔर अकर्म में कर्म को समझ सकते हैं। कर्म के विषय में महर्षि रमण का कहना था-"कर्म के\nमूल को ढूँढ़कर काट दीजिए। साक्षात्कार होने तक कर्म रहते हैं। आत्मसाक्षात्कार के पश्चात् न\nकर्म रहेगा, न विश्व।"\n')]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nविकर्म वे कर्म हैं, जो हमें नहीं करने चाहिएँ। ये घसीट कर हमें नरक में ले जाते हैं। इनसे\nअपना और समाज का भारी नुकसान होता है। कर्म की गति को न समझ पाने के कारण हम\nचोरी, व्यभिचार, हत्या, रिश्वतखोरी, धोखाधड़ी जैसे विकर्मों के कारण अनंत काल तक दु:ख-\nचक्रों में फँसे रहते हैं। ध्यान के परिपक्व हो जाने पर ही कर्म-चक्र की सच्ची अनुभूति होती है। संसार की नजरों में हम बच सकते हैं। कानून और न्यायालयों की प्रक्रिया से भी चालाकी से\nबचा जा सकता है, परंतु अपने मन से कोई नहीं बच सकता। मन रूपी प्राकृतिक शक्ति न्याय\nदेने में जरा-सी भी चूक नहीं करती है। अतः विकर्मों से सदा बचें और अपने कर्मों को यज्ञीय\nभाव से करें। यम के दूत और कोई नहीं, हमारे कर्मों पर लागू होने वाले प्रकृति के अटल नियम\nहैं। आगे के श्लोकों में योगेश्वर दिव्य पुरुष के लक्षण बताते हैं।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nजिन्हें नहीं होता अभिमान,"),l("br"),n._v("\nऋषिगण कहते उसे बुद्धिमान्। - 19"),l("br"),n._v("\nयोगी त्याग देता आसक्ति,"),l("br"),n._v("\nउसको मिल जाती है मुक्ति। - 20"),l("br"),l("br"),n._v("\nअंत:करण को जिस योगी ने जीता,"),l("br"),n._v("\nपाप रहित उसका जीवन होता। - 21"),l("br"),n._v("\nसुख -दुः ख में जिसकी समबुद्धि,"),l("br"),n._v("\nसमान लगती उसे सिद्धि-असिद्धि। - 22\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 19 से 22 तक योगेश्वर अर्जुन को सिद्ध पुरुष, पूर्ण योगी, दिव्य आत्मा के लक्षण बताते\nहैं। भगवान् कहते हैं कि दिव्य पुरुष वही है जिसने सभी कर्मों को ज्ञानाग्नि से भस्म कर दिया\nहै। रामचरितमानस में तुलसीदास ने भी इसकी पुष्टि करते हुए लिखा है–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nजोग अगिनि करि प्रगट तब कर्म सुभासुभ लाइ।\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\n“सभी कर्मों को योग रूपी अग्नि में भस्म कर दें।” योग अग्नि को प्रकट करने के लिए बुद्ध,\nमहावीर, शंकराचार्य, श्री रामकृष्ण परमहंस और श्री राम शर्मा आचार्य की तरह खूब तपना\nपड़ता है। अहंकार को जड़ से उखाड़कर 'मैं’, ‘मेरा' का भाव समाप्त करना होता है। नाम और\nवेश के महात्मा तो आजकल खूब हैं, परंतु सच्चे संतों का मिलना दुर्लभ हो गया है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nश्लोक 20 में योगेश्वर सिद्ध की पहचान बताते हुए कहते हैं कि उसे कर्मफल की कोई इच्छा\nनहीं होती है। वह सब कर्म केवल भगवान् के लिए करता है। गायत्री के सिद्ध साधक श्री राम\nशर्मा आचार्य जी अपनी अंतर्वेदना को व्यक्त करते हुए कहते हैं-"हम तो भगवान् (गुरुदेव) के\nहाथों में धागे की तरह बंधी कठपुतली मात्र हैं। वे जो निर्देश करें, वही करना, यही हमारे लंबे\nजीवन की एकमात्र रीति-नीति है। कभी दूसरी बात सोची ही नहीं, उनकी इच्छापूर्ति के\nअतिरिक्त और कुछ चाहा ही नहीं।"\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 21 में भगवान् का कहना है कि जिसने अपनी इंद्रियों, मन, बुद्धि को जीत लिया है, सभी\nभोगों को त्याग दिया है, धन का संग्रह नहीं करता है, वही सिद्ध आत्मा है। धर्म की आड़ में\nधन का संग्रह, व्यापार, व्यवसाय ने ही धार्मिक भ्रष्टाचार को जन्म दिया है। हमारे पूर्वज\nऋषियों ने धन का संग्रह करना तो दूर, विरासत में मिले धन को भी असार समझकर त्याग\nदिया था। यदि धर्म को जिंदा रखना है, तो साधु-महात्मा कहलाने वाले लोगों को केवल\nजीवनोपयोगी चीजें ही पास में रखनी चाहिएँ। धन का बीड़ा धनपतियों के पास ही रहने देना\nचाहिए। भगवान् बुद्ध की तरह कुटीर का जीवन ही सिद्ध महात्माओं को शोभा देता है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nश्लोक 22 में योगेश्वर इसी क्रम को जारी रखते हुए कहते हैं कि संत सदा संतुष्ट रहते हैं। सुख-\nदुःख, सिद्धि-असिद्धि में वे सहज ही बने रहते हैं। सिद्ध योगी जानते हैं कि सुख भी अनित्य है\nऔर दुःख भी अनित्य है। इन अनित्य चीजों के झमेले में मन को क्यों उलझाकर रखा जाए। जो\nनित्य तत्त्व है, उसी की खोज में मन को पूर्णतः क्यों न लगाया जाए। संत तुकाराम चरित्र में\nवर्णित है-“अच्छा हुआ भगवन्! दिवाला निकाला। दुर्भिक्ष ने ग्रासा, सो भी अच्छा ही किया।\nस्त्री मरी, तो भी अच्छा ही हुआ और यह जो दुर्दशा भोग रहा हूँ, सो भी अच्छा ही हुआ। गाय,\nबैल और द्रव्यादिक सब चला गया, यह भी अच्छा ही हुआ। लोकलाज नहीं रही, सो भी\nअच्छा हुआ और यह तो बहुत ही अच्छा हुआ भगवन्! जो मैं तेरी शरण में आ गया।" संत\nदुःख में भी अपना कल्याण समझकर सम और सहज बने रहते हैं।\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nजिसकी नष्ट हो गई आसक्ति,"),l("br"),n._v("\nकर्म से मिलती उसको मुक्ति। - 23\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयोगेश्वर कहते हैं कि जिसने अहंकार और आसक्ति को त्याग दिया है, जिसने पूर्ण ज्ञान प्राप्त\nकर लिया है और जो कर्मों को यज्ञ की भावना से करता है, उसे सिद्धावस्था, निर्वाण या परम\nअवस्था अवश्य मिलेगी। अहंकार और आसक्ति दो ऐसे मूल विकार हैं, जो अध्यात्म-विज्ञान की\nसबसे बड़ी बाधा हैं। अहंकार और आसक्ति हमारी अज्ञानता से उत्पन्न होते हैं। ज्ञान कैसे\nमिलेगा? अज्ञान कैसे हटेगा? इसका उत्तर भारत के ऋषियों ने हजारों वर्ष पूर्व खोज लिया\nथा। ध्यान से शुद्ध हुआ मन ही सत्य को जान सकता है। मन की इस अवस्था में हमें अनुभव\nहोगा कि हम सृष्टि की धारा का अभिन्न अंग हैं। हमारी तरह सभी प्राणी-पदार्थ भी इसी धारा\nमें बह रहे हैं। हम जबरदस्ती जिस पर मालिकाना हक जताकर आसक्ति पाले हुए हैं, वह\nहमारा न होकर प्रकृति का खजाना है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nमन की निरपेक्ष अवस्था में ही ज्ञान दृढ़ होता है। राग-द्वेष, अहंकार के समूल नाश से मन\nनिरपेक्ष भाव में स्थित होता है। जिस चीज को हमने स्वयं देख लिया, अनुभव कर लिया, फिर\nउसे जानने के लिए शास्त्रीय प्रमाण, उपदेश आदि की आवश्यकता नहीं रह जाती है। हम स्वयं\nसमझ जाते हैं कि स्वार्थ के लिए किया गया कर्म हमें कोल्हू के बैल की तरह घुमा रहा है और\nपरमार्थ भाव से किया गया कर्म हमारे मन और बुद्धि को परमावस्था की ओर ले जा रहा है।\nयह मुक्त अवस्था, स्वतंत्रता केवल मानव योनि में प्राप्त करनी संभव है, क्योंकि अन्य\nयोनियों में मन और बुद्धि बद्ध होते हैं। इसके पश्चात् योगेश्वर भिन्न-भिन्न प्रकार के यज्ञों के\nविषय में बताते हैं।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nयज्ञ पदार्थ, पात्र होते ब्रह्मरूप,"),l("br"),n._v("\nयोगी यज्ञ करते विविध रूप। - 24"),l("br"),n._v("\nब्रह्म-अग्नि में करते आपा अर्पण,"),l("br"),n._v("\nइंद्रिय संयम से होता नियंत्रण। - 25-26"),l("br"),l("br"),n._v("\nप्राण साध से होती समाधि,"),l("br"),n._v("\nयोगी देते द्रव्य की आहुति।"),l("br"),n._v("\nसमभाव में सदा वे रहते,"),l("br"),n._v("\nस्वाध्याय यज्ञ नित्य करते। - 27-28"),l("br"),l("br"),n._v("\nपूरक, कुम्भक से होता यज्ञ,"),l("br"),n._v("\nनियमित आहार को कहते यज्ञ।"),l("br"),n._v("\nत्याग, तपस्या से होता है यज्ञ,"),l("br"),n._v("\nपाप का नाश करते सभी यज्ञ। - 29-30\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 24 से 30 तक भगवान् ने यज्ञ को विस्तृत अर्थ देते हुए हमारे जीवन के सभी\nक्रियाकलापों को यज्ञ से जोड़ दिया है। श्लोक 24 में वे कहते हैं कि यज्ञ में काम आने वाली\nसभी वस्तुएँ, कर्ता व क्रियाएँ ब्रह्म का अंश हैं। इस भाँति कर्म करने से ही ब्रह्म का अनुभव\nहोता है। यज्ञ भारतीय संस्कृति का जनक है। सभी आर्ष-ग्रन्थों में यज्ञों की महिमा का वर्णन है।\n‘शतपथ ब्राह्मण’ में यज्ञों के अनेक विधि-विधान हैं। ‘शतपथ’ में यज्ञ को देवताओं की आत्मा,\nश्रेष्ठ कर्म और सृष्टि की धुरी कहा गया है। सृष्टि में यज्ञ क्षणभर के लिए भी नहीं रुकता है।\nगुरुत्वाकर्षण बल से अधिक शक्तिशाली अपने से कम शक्ति वाले को खींचकर गतिमान् बनाता\nहै। सभी ग्रह, नक्षत्र, आकाशगंगाएँ और निहारिकाएँ एक-दूसरे को शक्ति का आदान-प्रदान\nकरते हैं। परस्पर सहयोग के इसी नैसर्गिक यज्ञ से धरती पर जीवन संभव है। क्षुद्र कहे जाने\nवाले प्राणी और पेड़-पौधे भी दूसरों को देकर यज्ञ का बोध करा रहे हैं। मानव शरीर के सभी\nअंग भी एक दूसरे का पोषण और रक्षण करते हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 25 से 30 तक योगेश्वर ने बारह प्रकार के यज्ञ बताए हैं। कुछ यज्ञ जनसुलभ हैं। प्रतिदिन\nअच्छी पुस्तकें पढ़कर स्वाध्याययज्ञ नियमित रूप से किया जा सकता है। बहुत कम घरों में\nआजकल ऋषि-साहित्य मिलता है। नियमित आहार से अनुशासनयज्ञ आज की सबसे बड़ी\nआवश्यकता है। जर्जर होते मानव स्वास्थ्य की रक्षा का यह प्रमुख साधन है। स्वस्थ कौन है?\nमहर्षि चरक के प्रश्न का जवाब देते हुए वाग्भट्ट कहते हैं- (हितभुक, मितभुक, ऋतभुक) जो\nहितकारी हो, भूख से कम, मौसम के अनुसार और न्याय-नीति से कमाया गया हो, वही\nभोजन हमें स्वस्थ रख सकता है। भगवान् बुद्ध का भी कहना था कि हमें आहार की मात्रा की\nजानकारी होनी चाहिए और हमें आहार में आसक्त नहीं होना चाहिए।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयदि हम इन दो यज्ञों को अच्छी प्रकार कर लेते हैं, तो देवयज्ञ, ज्ञानयज्ञ, संयमयज्ञ, विषययज्ञ,\nसमाधियज्ञ, द्रव्ययज्ञ, तपोयज्ञ, प्राणायामयज्ञ भी कर सकते हैं। ऋषि दयानंद ने भी\nऋग्वेदादिभाष्यभूमिका और सत्यार्थप्रकाश में पाँच महायज्ञों को दैनिक जीवन में आवश्यक\nबताया है-“ब्रह्मयज्ञ, (शास्त्र पढ़ना-पढ़ाना) देवयज्ञ, (अग्निहोत्र, विद्वानों की सेवा और संगत\nकरना) पितृयज्ञ, (माता-पिता, ज्ञानी और योगियों की सेवा करना) वैश्वदेव (पाकशाला के\nभोजन का अंश पाकाग्नि में होमना) और पाँचवाँ अतिथि की सेवा करना।”\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 30 के अंत में योगेश्वर कहते हैं कि उपर्युक्त यज्ञों को करने वाले योगी पापों का नाश कर\nदेते हैं और वे यज्ञ के गूढ़ अर्थ को जानते हैं। यज्ञ एक महाविज्ञान है। प्राचीन काल में इस विद्या\nके द्वारा अनेक महान् प्रयोजन सिद्ध किए गए थे। भगवान् राम का जन्म श्रृंगी ऋषि के द्वारा\nकिए गए पुत्रेष्टि यज्ञ से हुआ था। विश्व समस्याओं के समाधान के लिए बड़े स्तर पर अश्वमेध\nयज्ञ, राजसूय यज्ञ, वाजपेय यज्ञ कराए जाते थे। रावण को भी यज्ञ की शक्ति पर बहुत अधिक\nविश्वास था, इसलिए वह अपने अनुचरों को यज्ञ को विध्वंस करने का आदेश देता था। उसके पुत्र मेघनाद ने भी वानरों की शक्ति को नष्ट करने के लिए एक यज्ञ शुरू किया था। विभीषण\nऔर लक्ष्मण ने सेना के साथ जाकर उसका विध्वंस किया था।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nआधुनिक युग में ऋषि दयानंद ने धूमिल होती हुई यज्ञ-विद्या को पुनर्जीवित किया। बीसवीं\nशताब्दी में युग ऋषि श्री राम शर्मा आचार्य जी ने प्राचीन काल की तरह देश-विदेश में सैकड़ों\nअश्वमेध यज्ञ संपन्न किए। अभी भी इन यज्ञों का सिलसिला जारी है। शांतिकुंज हरिद्वार की\nयज्ञशाला में प्रतिदिन स्त्रियों के द्वारा यज्ञ कराया जाता है। नारी-सशक्तिकरण की दिशा में\nउनकी यह अभूतपूर्व पहल है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nयज्ञ को वैज्ञानिक रूप देते हुए श्री राम शर्मा आचार्य जी ने ‘यज्ञ का ज्ञान-विज्ञान’ शीर्षक में\nलिखा है-"यज्ञ का विज्ञान अत्यंत महत्त्वपूर्ण है। वेदमंत्रों की शब्द शक्ति को विशिष्ट कुंडों,\nसमिधाओं, हवियों, चरुओं के साथ उत्पन्न की हुई विशेष सामर्थ्यवान् अग्नि में सम्मिलित कर\nदेने पर रेडियो सक्रिय तरंगों का आविर्भाव होता है। ये शक्ति-तरंगें रडार यंत्र की तरह संसार\nके किसी भी भाग में भेजी जा सकती हैं। प्रकृति में किसी विशेष प्रयोजन के लिए प्रविष्ट कराई\nजा सकती हैं। पृथ्वी पर वर्षा, धन-धान्य, दूध, आरोग्य, प्राण, जीवन आदि की अभिवृद्धि\nकराने के लिए इनका उपयोग किया जा सकता है।"\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्री राम शर्मा आचार्य जी के उपर्युक्त वक्तव्य से सिद्ध होता है कि यज्ञ से मानव मन में और\nसूक्ष्म जगत् में व्याप्त पाप-दोषों एवं विषाक्तता को दूर किया जा सकता है। अतः भगवान्\nकृष्ण के इस आदेश को शिरोधार्य करते हुए हमें यज्ञ को अपने जीवन का अभिन्न अंग बना\nलेना चाहिए। इससे आधुनिक युग की समस्याओं जैसे प्रदूषण, तनाव, चिंता, असाध्य रोगों\nआदि से अवश्य मुक्ति मिलेगी। यज्ञ के परिणाम बताते हुए योगेश्वर कहते हैं–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nयज्ञ शेष से मिलती शांति,"),l("br"),n._v("\nपरम मुक्ति की होती प्राप्ति।"),l("br"),n._v("\nयज्ञविहीन का दुःखदायक लोक,"),l("br"),n._v("\nकैसे होगा सुखदायक परलोक? - 31\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nहे कुरुश्रेष्ठ! यज्ञ शेष अमृत है, इसी से परम शांति की प्राप्ति होती है। जिनमें यज्ञीय भाव\nअर्थात् देने का भाव नहीं है, अपने धन को अपने से ही चिपकाए रहते हैं, उनका लोक और\nपरलोक दुःखदायक होता है। हम प्रकृति के अंतराल में झाँककर देखें, यहाँ कोई गठरी\nसंभालकर नहीं बैठा है। पशु-पक्षी भी प्रकृति से केवल अपने जीवनयापन लायक लेते हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nमनुस्मृति (3.118) में वर्णित है-“यज्ञशिष्टाशिनं ह्येतत्सतामन्नं विधीयते।”यज्ञ-शेष ही सज्जनों\nका अन्न है। हमारे ऋषि-मुनियों ने प्रकृति की गोद में बैठकर उतना ही लिया, जो जीवनयापन\nके लिए अत्यंत जरूरी था। यदि आजकल के सभी धार्मिक, साधु और संत कहे जाने वाले लोगों\nने भी भगवान् श्रीकृष्ण की इस बात को माना होता, तो वे धर्म की आड़ में धन-संग्रह करने का\nपापाचार कभी नहीं करते। इस श्लोक में भगवान् कहते हैं कि यज्ञ न करने वाले का लोक-\nपरलोक दुःखदायक होता है। जितना अधिक हम धन, वस्तुओं का संग्रह करेंगे, उनकी रक्षा को\nलेकर भी चिंता बनी रहेगी। कम हो जाने, खो जाने का डर सताता रहेगा। ईशावास्योपनिषद्\n(1) का कथन है–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nतेन त्यक्तेन भुञ्जीथा मा गृधः कस्य स्विद्धनम्।।\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\n“केवल भगवान् द्वारा छोड़े गए का (यज्ञशेष) ही उपयोग करो। अधिक का लालच मत करो।\nक्योंकि यह धन किसका है? अर्थात् किसी मनुष्य का नहीं केवल ईश्वर का है।” मनुष्य प्रकृति\nका सबसे समझदार प्राणी है। उससे अपेक्षा की जाती है कि वह अतिरिक्त धन से अन्य की रक्षा\nकरे।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nजो जल बाढ़ै नाव में, घर में बाढ़ै दाम।"),l("br"),n._v("\n\nदोनों हाथ उलीचिये, यही सयानो काम।। - संत कबीर\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nकबीर साहब ने बहुत ही सुंदर शब्दों में कहा है कि यदि नाव में जल और घर में धन बढ़ने\nलगे, तो उसे दोनों हाथों से खाली कर दो। भामाशाह ने महाराणा प्रताप की सेना के पुनर्गठन\nके लिए अपने धन के दरवाजे खोल दिए थे। आज हम सभी उन्हें आदरपूर्वक याद करते हैं।\nलोक और परलोक में उनकी कीर्ति सदा छाई रहेगी। आगे के दो श्लोकों में यज्ञीय कर्म का संदेश\nहै।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nयज्ञीय कर्म ईश्वर की शक्ति,"),l("br"),n._v("\nइसी ज्ञान से मिलती मुक्ति। - 32\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 32 में यज्ञ के ज्ञान-विज्ञान को और भी अधिक स्पष्ट करते हुए भगवान् श्रीकृष्ण कहते हैं\nकि ये सभी यज्ञ मेरे मुख से ( वेदों ) निकले हैं। सभी यज्ञ कर्म से ही होते हैं। कर्म को यदि\nयज्ञीय बना दिया जाए, तो हम मुक्त हो सकते हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nअहंकारवश हम समझ बैठते हैं कि कर्मों के कर्ता हम हैं। हमारे कर्म स्वार्थपूर्ण होने के कारण\nयज्ञीय रूप नहीं लेते हैं। अहंकार हमारे चित्त (प्राण) की सबसे अधिक गहराई में बैठा है। इसे\nकेवल ध्यान की शिला पर बैठकर जाना जा सकता है। अहंकार के हटते ही प्रकृति का दरवाजा\nखुल जाता है। सब जगह केवल ब्रह्म ही दिखाई पड़ता है। इसी श्लोक\nमें योगेश्वर ने यह भी कहा है कि कर्म के विज्ञान को समझकर तुम मुक्त हो सकते हो। दासता\nईश्वर प्रदत्त नहीं है। यह हमारी अज्ञानता का परिणाम है। अतः हम स्वयं ही इससे मुक्त हो\nसकते हैं। अहंकार अति सूक्ष्म होता है, इस कारण इसमें फेरबदल के लिए बुद्ध, शंकराचार्य की\nतरह तपना पड़ता है। तप की भट्ठी में अहंकार को भस्मीभूत करके ही हम अपनी दासताओं से\nमुक्त होकर ब्रह्मस्वरूप हो सकते हैं। मुक्त अवस्था में प्रकृति के पंजे से छूटकर हम ब्रह्म के साथ\nएकाकार हो जाते हैं। यह सुख-दुःख से परे निरंतर आनंद की अवस्था है। ज्ञानयज्ञ की महिमा\nका वर्णन करते हुए योगेश्वर कहते हैं–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nज्ञानयज्ञ का फल अनंत,"),l("br"),n._v("\nकर्म का होता ज्ञान में अंत। - 33\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयोगेश्वर कहते हैं कि द्रव्ययज्ञ, देवयज्ञ, दानयज्ञ आदि सभी यज्ञों में ज्ञानयज्ञ श्रेष्ठ है। सभी कर्म\nज्ञान में जाकर समाप्त हो जाते हैं। आध्यात्मिक ज्ञान चेतनापरक है। चेतना से ही पदार्थ बना\nहै। अतः चेतना की सही-सही जानकारी से ही सत्य प्रकट होता है। ब्रह्माण्ड के अंतिम सूक्ष्म\nसत्य ब्राह्मी चेतना को अनुभव करके उसके साथ एक हो जाना ही वास्तविक ज्ञान है। यदि वैज्ञानिक यंत्रों से ब्रह्म-तत्त्व को जान भी लिया जाए, तो इससे कुछ भौतिक लाभ हो सकते हैं,\nपरंतु इस प्रकार के ज्ञान से कोई बुद्ध नहीं हो सकता। भगवान् बुद्ध और जगद्गुरु शंकराचार्य\nमहाज्ञानी थे। उन्होंने ज्ञान पाने के लिए मन द्वारा चेतना पर महाप्रयोग किए थे।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nधम्मपद (96.282) में वर्णित है-"सम्यक् ज्ञान द्वारा मुक्त हुए अरहंत का मन शांत हो जाता है\nऔर वाणी तथा कर्म भी शांत हो जाते हैं। योग के अभ्यास से प्रज्ञा (ज्ञान) उत्पन्न होती है। योग\nतथा अयोग इन दो प्रकार के मार्गों को जानकर अपने आपको इस प्रकार विनियोजित करें\nजिससे प्रज्ञा की भरपूर वृद्धि हो।"\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nविवेक-चूडामणि (47) में शंकराचार्य ने भी इसी प्रकार का भाव व्यक्त करते हुए कहा है-"तू\nवस्तुतः परमात्मा ही है, परंतु अज्ञान से जुड़ जाने के कारण अनात्म के बंधन में पड़ गया है\nऔर इसी से तेरा जन्म-मृत्यु रूपी संसार-चक्र चल रहा है।”\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nआधुनिक मनुष्य के पास जानकारियाँ बहुत हैं, परंतु वह उनके बोझ से दबकर तनाव का\nशिकार हो गया है। अब उसे ज्ञान की सख्त जरूरत आन पड़ी है। ये मनुष्य का परम सौभाग्य\nहै कि उसने महर्षि पतंजलि के अष्टांग योग के द्वारा ज्ञान प्राप्ति का मार्ग अपना लिया है। श्लोक\n34 से 42 तक ज्ञान के महत्त्व पर प्रकाश डाला गया है।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nसरल भाव से समझ परमात्मा,"),l("br"),n._v("\nउपदेश देंगे तुझे ज्ञानी महात्मा। - 34\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nभगवान् कृष्ण अर्जुन को कहते हैं कि सेवा, समर्पण भाव से ज्ञानी महात्माओं की शरण में जा।\nवे तुझे उपदेश देंगे। भगवान् श्रीकृष्ण तो स्वयं गुरु रूप में अर्जुन के साथ थे। इस श्लोक में उनका\nयह संदेश हम सबके लिए है। भारतीय संस्कृति में गुरु-शिष्य परंपरा का विशेष महत्त्व रहा है।\nनचिकेता तीन रात्रि तक बिना भोजन किए यम के आने की प्रतीक्षा करते रहे। ब्रह्मर्षि नारद\nको ब्रह्मनिष्ठ सनत्कुमार जी ने उपदेश दिया था। उपनिषदों में इसी प्रकार के और भी अनेक\nकथानक हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nआधुनिक युग में ऋषि दयानंद और ऋषि विवेकानंद ने भी गुरु के चरणों में बैठकर ज्ञान पाया\nथा। गुरु शिष्य को अग्नि परीक्षा से गुजारकर तथा प्राणशक्ति के दान द्वारा उनमें देवत्व का\nउदय करते थे। विश्वामित्र और देवर्षि नारद की भी देवों ने कड़ी परीक्षा ली थी। विवेकानंद\nजी को भी अनेक संकट के दरिया पार करने पड़े थे। संत कबीर जी ने गुरु की महिमा का वर्णन\nकरते हुए कहा है–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nगुरु कुम्हार शिष कुंभ है, गढ़ि-गढ़ि काढ़ै खोट।"),l("br"),n._v("\n\nअंतर हाथ सहार दे, बाहर बाहै चोट।।\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\n“गुरु कुम्हार है और शिष्य घड़ा है, भीतर से हाथ का सहारा देकर, बाहर से चोट मार-मार\nकर और गढ़-गढ़ कर शिष्य की बुराई को गुरु निकालते हैं।”श्री विरजानंद जी का स्वामी\nदयानंद सरस्वती के प्रति बहुत कठोर व्यवहार रहता था। छोटी-सी गलती के लिए भी वे उन\nपर छड़ी से प्रहार कर देते थे।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nआज धर्म में आडंबर, दिखावा और विलासिता के कारण गुरु-शिष्य परंपरा धूमिल पड़ गई है।\nअब वशिष्ठ, द्रोणाचार्य, महर्षि विरजानंद, श्री रामकृष्ण परमहंस जैसे गुरुओं का मिलना\nअसंभव हो गया है। ज्ञान मनुष्य की सबसे बड़ी भूख है। ज्ञान के अभाव में सांसारिक सुखों के\nबीच भी वह प्यासा-सा बना रहता है। अब प्रश्न पैदा होता है कि हम गुरुओं के बिना ज्ञान कैसे\nपाएँगे? अध्यात्म विज्ञान भौतिक विज्ञान की तुलना में अधिक कठिन है क्योंकि इसका संबंध\nमन, बुद्धि और आत्मा जैसी महाशक्ति से जुड़ा हुआ है। अतः इसमें मार्गदर्शन की सदा\nआवश्यकता रहेगी ही।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nमहर्षि रमण का कथन है-"गुरु मनुष्य रूप में हो यह जरूरी नहीं है।भक्त के आत्मसमर्पण करने\nपर ईश्वर गुरु रूप में प्रकट होकर दया करते हैं।" हमारे अंदर यदि नचिकेता और विवेकानंद\nजैसी ईश्वर को पाने की भूख हो, तो सूक्ष्म देहधारी महान् आत्माएँ हमें तलाशती हुई स्वयमेव\nगुरु रूप में प्रकट हो जाएँगी। इस सत्य को हम कुछ वर्षों तक कठोर साधना और तप करके\nस्वयं अनुभव कर सकते हैं।श्री रामकृष्ण परमहंस गुरु ढूँढ़ने कहीं नहीं गए थे। जगदंबा की कृपा\nसे उन्हें उचित समय पर सहायता मिलती रही। धार्मिक-भ्रष्टाचार के बोल-बाले में हमें निराश\nहोने की आवश्यकता नहीं है। खुद-ब-खुद योग का अभ्यास शुरू कर दें। हमारी मेहनत, लगन,\nश्रद्धा, निष्ठा को देखकर ईश्वर के लंबे हाथ गुरु रूप में अवश्य पहुँचेंगे।\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nज्ञान से तेरा मोह मिटेगा,"),l("br"),n._v("\nसब जग मुझमें देख सकेगा। - 35"),l("br"),n._v("\nयदि तू मानता स्वयं को पापी,"),l("br"),n._v("\nज्ञान नौका से दूर कर भ्रांति। - 36"),l("br"),n._v("\nअग्नि ईंधन को कर देती खाक,"),l("br"),n._v("\nज्ञान से कर तू कर्मों को राख। - 37\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nज्ञान की उपयोगिता बताते हुए योगेश्वर कहते हैं कि अर्जुन इससे तेरा मोह मिट जाएगा और\nसंपूर्ण संसार को मुझमें देख सकेगा। बिना ज्ञान के हमें अपने परिवार के लिए जीने में ही लाभ\nनजर आता है। हमारी सोच का दायरा सीमित होने के कारण स्वार्थ, लोभ, क्रोध, भ्रष्टाचार\nपनपता है। मछली ज्ञान की लघुता के कारण समुद्र में जन्म लेकर भी उसकी विशालता से\nअनभिज्ञ रहती है। अज्ञानता के कारण हमारी स्थिति भी कुछ ऐसी ही है। परंतु हम पशु-\nपक्षियों की तुलना में अधिक चेतनशील होने के कारण अज्ञान के ढक्कन को उठाकर सत्य का\nसाक्षात्कार कर सकते हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nमहाबोधि के दिन भगवान् बुद्ध अपने अंदर ही सभी जीवों के जन्म-मरण को अनुभव कर रहे\nथे। उनकी सूक्ष्म-देह सभी सीमाओं को पार कर ब्रह्माण्ड की तरह अनंत और विशाल हो गई\nथी। अब उनके लिए कुछ जानना शेष नहीं रह गया था। मन की इस अवस्था को ही ज्ञान,\nमुक्ति और निर्वाण कहा जाता है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nज्ञान और मुक्ति हमारे अंदर पहले से ही मौजूद हैं। उन्हें कहीं दूर से खोजकर नहीं लाना है।\nहम जन्म से ही ईश्वर की तरह शुद्ध, बुद्ध और पवित्र हैं। मैं, मेरा करते-करते अनेक अशुभ\nकर्मों के कारण प्राण में जो मिलावट हो गई है, बस उसे ही दूर करना है। प्राण को शुद्ध करने\nके लिए भारतीय योग विज्ञान ने प्राचीनकाल से ही अनेक विधि-विधानों की खोज कर ली थी। यम-नियम और आष्टांगिक मार्ग के पालन से चित्त (प्राण) के सभी मलों को हटाया जा\nसकता है। योग के अंगों का अनुष्ठान करने से अशुद्धि का नाश और ज्ञान का प्रकाश होता है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nआज बहुसंख्यक लोग अनेक मनोरोगों के शिकार हैं, जिसका कारण हमारे द्वारा प्राण में बोए\nहुए अनावश्यक और विकृत विचार हैं। प्राण अति सूक्ष्म है। वैज्ञानिक यंत्रों से इसे आज तक\nदेख पाना संभव नहीं हो पाया है। हमारे विचार सूक्ष्म तरीके से प्राण में जाकर छिप जाते हैं।\nशास्त्रों में इसे ही चित्रगुप्त कहा गया है। यह गुप्त प्राण ही दिन-रात काम करके गोपनीय तरीके\nसे हमारे कर्मों की फाइलें तैयार करता रहता है। चिकित्सा-विज्ञान ने भी ध्यान की उपयोगिता\nको स्वीकार करके इसे चिकित्सा-प्रणाली का अंग बना लिया है।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nधीरे-धीरे संसार योग-युग की ओर बढ़ रहा है। श्लोक 36 में योगेश्वर कहते हैं कि अर्जुन यदि तू\nयह मानता है कि मैं तो पापी हूँ और मैंने बहुत सारे निंदनीय कर्म कर दिए हैं, तो भी ज्ञान के\nद्वारा सभी भ्रमों को दूर कर सकता है। संसार का चलन कुछ ऐसा है, जिसमें भोग सिर चढ़कर\nबोलते हैं। यदि कोई योग के पथ पर कदम बढ़ाए, तो लोग अनेक तर्क देकर उसे निरुत्साहित\nकरते हैं। भगवान् इन सब भ्रांतियों को दूर करते हुए कहते हैं कि ज्ञान की नौका में पापी-से-\nपापी भी किसी भी उम्र में बैठ सकता है। डाकू अंगुलिमाल और आम्रपाली भगवान् बुद्ध के\nचरणों में समर्पण कर धन्य हो गए थे। ज्ञान के पथ पर कभी भी आरूढ़ हो सकते हैं। श्रृंगी\nऋषि के श्राप के कारण राजा परीक्षित की मृत्यु के सात दिन ही शेष रह गए थे। इन सात\nदिनों का सदुपयोग करते हुए उन्होंने मृत्यु का भय मन से निकालकर शुकदेव मुनि से ज्ञान\nप्राप्त किया था।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयहाँ एक प्रश्न मन में उठ सकता है कि क्या हमने जो अशुभ और पाप कर्म कर दिए हैं, वे बिना\nभोगे नष्ट हो सकते हैं? कर्म करने के बाद उसकी प्रक्रिया हमारे अर्धचेतन मन में चलती रहती\nहै। इस प्रक्रिया के अंतिम चरण में मन संचित विचार समूह को सुख-दुःख, रोग, शोक, आधि-\nव्याधि, दुर्घटना आदि के रूप में बाहर निकाल देता है। अतः किए गए कर्मों के भोगों से भागने\nका कोई तरीका सृष्टि में नहीं है। हमारे ऋषियों ने योग-विज्ञान के द्वारा कुछ ऐसे तरीके ढूँढ़\nनिकाले थे, जिनसे कर्म भोगों को कम किया जा सकता है। उनकी समयावधि घटाई जा सकती\nहै। शांत मन में कर्मभोग के निकलने की प्रक्रिया युद्धस्तर पर चलती है। जन्म-जन्मांतर तक\nभोगे जाने वाले भोग कुछ दिनों, महीनों में शरीर से बाहर निकल जाते हैं और पहाड़ जैसा\nभोग टीले के समान हो जाता है।\nश्लोक 37 में भगवान् श्रीकृष्ण कहते हैं कि जिस प्रकार अग्नि ईंधन को राख कर देती है, उसी\nप्रकार ज्ञानाग्नि से कर्म भोगों को भस्मीभूत कर दो। योग की ऊँची कक्षाओं में सभी जन्मों के\nकर्मबीजों को जलाकर नष्ट कर दिया जाता है, परंतु इसके लिए महर्षि अरविंद घोष और ऋषि\nदयानंद की तरह घनघोर श्रम करना होता है। योग के संबंध में महर्षि पतंजलि का अनुभव\nबहुत गहरा है। वे योग-दर्शन के साधनपाद (52) में कहते हैं–\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nततः क्षीयते प्रकाशावरणम्।।\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\n“जैसे-जैसे मनुष्य योग का अभ्यास करता है, उसके संचित कर्म-संस्कार दूर होते जाते\nहैं।” एक कुशल मार्गदर्शक की भाँति भगवान् श्रीकृष्ण ने भोगों को घटाने की वैज्ञानिक प्रक्रिया\nबता दी है। अब तक्षशिला और नालंदा जैसे योग विश्वविद्यालय खुल ही जाने चाहिएँ। इसी से\nलड़खड़ाती हुई मनुजता की रक्षा हो सकती है। आगे के श्लोकों में ज्ञान और अज्ञान का वर्णन है।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nज्ञान समान नहीं पवित्र निधि,"),l("br"),n._v("\nइसका साधन योग विधि। - 38"),l("br"),n._v("\nज्ञान को प्राप्त करते श्रद्धावान्,"),l("br"),n._v("\nवे पाते सुख, शांति, भगवान्। - 39"),l("br"),l("br"),n._v("\nअज्ञानी हो जाते पथ भ्रष्ट,"),l("br"),n._v("\nलोक-परलोक हो जाता नष्ट। - 40"),l("br"),n._v("\nजिसने नष्ट कर दिया कर्म संबंध,"),l("br"),n._v("\nकर्म से नहीं जाता वह बंध। - 41\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nगीता का अध्याय चार ज्ञानयोग पर आधारित है।भगवान् ने बार-बार ज्ञान का महत्त्व\nबताकर, हमारे मन को पिघलाकर, उसमें ज्ञान का बीज बोने की कोशिश की है। श्लोक 38 में\nउनका कहना है कि पढ़ने, सुनने व तर्कबाजी से ज्ञान प्राप्त नहीं होगा। ज्ञान के लिए हमें योग\nको सिद्ध करना होगा। एक विद्यार्थी की तरह हमें कमर कसकर जुट जाना होगा। योग के\nअन्वेषक महर्षि पतंजलि नपे-तुले शब्दों में योग को सिद्ध करने के तरीके बताते हैं। साधनपाद\nके सूत्र 29 में वे यम-नियमों के पालन द्वारा स्थूल तरीके से योग सिद्ध करना सिखाते हैं। धीरे-\nधीरे सूक्ष्म की ओर बढ़ते हुए विभूतिपाद के सूत्र चार और सात में कहते हैं कि ध्यान, धारणा\nऔर समाधि से योग की सिद्धि होगी। हमारे अंग-अंग में योग के प्रति दृढ़ धारणा होनी\nचाहिए।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 39 में योगेश्वर कहते हैं कि संयमशील और श्रद्धावान् ही ज्ञान प्राप्त करते हैं और ज्ञान से\nउनको परम शांति मिलती है। किसी विषय पर हमारे व्यवहार, विचार और भावनाओं में\nएकाग्रता को श्रद्धा कहा जाता है। श्रद्धा पत्थर में भी प्राण भर देती है।\n")]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nभवानीशंकरौ वन्दे श्रद्धा विश्वासरूपिणौ। – बालकांड\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\n“श्रद्धा और विश्वास के स्वरूप पार्वती जी और शंकर जी की मैं वंदना करता हूँ।” तर्क से हम\nसंदेह से घिर जाते हैं। अभ्यास और अनुभव से ही हमारी श्रद्धा का पोषण होता है। उठते-\nबैठते, सोते-जागते हमारा ध्यान लक्ष्य पर होना चाहिए। अर्जुन की तरह केवल मछली की\nआँख ही दिखाई देनी चाहिए और आसपास हमें कुछ नहीं दिखना चाहिए। हर क्षण सजग और\nसावधान रहने से ही योग सिद्ध होगा। योग के सिद्ध होते ही हम परम शांति अनुभव करेंगे।\n‘आत्मानुभूति तथा उसके मार्ग’ में स्वामी विवेकानंद ने लिखा है-“अगर मनुष्य अपने अहम्\nबोध को अनंत गुणा कर दे, तो वह परमेश्वर बन सकता है और संपूर्ण विश्व पर अपना\nअधिकार जमा सकता है।"\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nयोगेश्वर कहते हैं कि अज्ञान से मनुष्य भ्रष्ट हो जाता है और उसका इहलोक तथा परलोक भी\nबिगड़ जाता है। असत्य को सत्य और सत्य को असत्य मानना ही अज्ञान है। अज्ञान से छुटकारे के लिए हमें अपने अंतर्मन को प्रकाशित कर निरपेक्ष अवस्था में पहुँचना होगा। योग-सिद्धि से\nअपनी शक्तियों को अनंत गुणा करके ही हम अपने व्यक्तिगत ‘मैं’ को समष्टिगत ‘मैं’ में\nविसर्जित करके अज्ञान के पर्दे को उठा सकते हैं।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nभगवान् का कहना है कि जिसने कर्मयोग के द्वारा कर्म संबंध नष्ट कर दिए हैं, फिर कर्म उसके\nलिए बंधन का कारण नहीं बनते हैं। कर्म का अच्छा-बुरा परिणाम ही हमें जन्म-मृत्यु और\nअनेक योनियों में भटकने के लिए बाध्य करता है। महर्षि अरविंद घोष योग के आधुनिक\nवैज्ञानिक थे। ‘योग समन्वय’ में कर्म के विषय में वे कहते हैं-"जब मनुष्य का योग एक निश्चित\nशिखर पर पहुँच जाता है, तब उसके लिए कोई कर्म शेष नहीं रह जाता। तब वह कर्म उसी\nप्रकार करता है जिस प्रकार भगवान् कर्म करते हैं। भागवत-शक्ति ही उसके अंदर उसकी प्रकृति\nद्वारा कर्म करती है। उसका कर्म परा-शक्ति के सहज प्रभाव के द्वारा विकसित होता है।” अंतिम\nश्लोकों में योगेश्वर अज्ञान मिटाने और युद्ध का आदेश देते हैं।\n')]),n._v(" "),l("h3",{staticClass:"text-2xl text-center leading-10 text-yellow-700 py-10"},[n._v("\nज्ञान से अज्ञान हटाओ,"),l("br"),n._v("\nअपने संशय सब मिटाओ।"),l("br"),n._v("\nयुद्ध निमित्त अर्जुन उठो,"),l("br"),n._v("\nसमता योग में तुम जुटो। - 42\n")]),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nश्लोक 42 में योगेश्वर अपनी ओजस्वी वाणी से अर्जुन को युद्ध और योग के लिए प्रेरित करते हैं।\nकुरुक्षेत्र की युद्ध-भूमि और योग परस्पर विरोधी बातें लग रही हैं। योग किसी एक वर्ग विशेष\nतक सीमित नहीं है। केवल भगवा वस्त्रधारी संन्यासी को ही योगी नहीं कहा जा सकता। योग\nमन की एक उच्च अवस्था का नाम है, जिसमें हमारे विचार और कर्म स्वार्थ से ऊपर उठकर\nपरमार्थ के लिए होते हैं। अतः जिस व्यक्ति के हृदय में समता है, विश्व-बंधुत्व की भावना है,\nउसका प्रत्येक कर्म योग ही है। महाराणा प्रताप की युद्ध साधना योगियों जैसी थी। परिवार\nसमेत भूखे मरकर और अनेक कष्ट सहकर भी उन्होंने अन्याय का समर्थन नहीं किया।\n")]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v('\nस्वस्थ, सभ्य और शक्तिशाली समाज के लिए योग अपरिहार्य आवश्यकता है। भगवान् कभी-\nकभी सोई हुई मनुजता को जगाने और उठाने के लिए आते हैं। जब हम चारों ओर से घिर जाते\nहैं, कोई रास्ता नहीं सूझता है, तो भगवान् की कोई-न-कोई शक्ति हमारा मार्गदर्शन करने के\nलिए धरा पर आ जाती है। स्वामी विवेकानंद देश-विदेश में घूम-घूमकर लोगों को जगा रहे थे।\nउनका कहना था-"जगत् की अनंत शक्ति तुम्हारे भीतर है। जो कुसंस्कार तुम्हारे मन को ढके\nहुए हैं, उन्हें भगा दो। साहसी बनो। सत्य को जानो और उसे जीवन में परिणत करो। उठो,\nजागो, जब तक ध्येय तक न पहुँचो, तब तक मत रुको। पिंजरे को तोड़ डालने वाले सिंह की\nभाँति तुम अपने बंधन तोड़कर सदा के लिए मुक्त हो जाओ। तुम्हें किसका भय है, तुम्हें कौन\nबांधकर रख सकता है? तुम शुद्धस्वरूप हो, नित्यानंदमय हो।"\n')]),n._v(" "),l("br"),n._v(" "),l("p",{staticClass:"text-2xl leading-10 font-light tracking-wide"},[n._v("\nइस अध्याय में योगेश्वर ने अर्जुन के अंदर जो ज्ञान का बीज बोया है, वह प्रस्फुटित, पल्लवित\nऔर पुष्पित होने लगा है। अर्जुन के मन में अनेक प्रश्न उठने लगे हैं। इन्हीं प्रश्नों से पाँचवाँ\nअध्याय आरंभ होता है।\n")])])}],!1,null,null,null);t.default=component.exports}}]);